mapper.select:
select p.* from bms_permission p where p.deleted=0
and p.permission_type=#{permissionType}
order by p.sort

select p.* from bms_permission p left join bms_role_permission rp on p.permission_id=rp.permission_id left join bms_user_role ur on ur.role_id=rp.role_id where ur.user_id=#{userId} and p.deleted=0 and rp.deleted=0 and ur.deleted=0
and p.permission_type=#{permissionType}
order by p.sort
